mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM comment_likes
    WHERE user_id = #{userId}
      AND comment_id = #{commentId}
)

mapper.delete:
DELETE FROM comment_likes
WHERE user_id = #{userId}
  AND comment_id = #{commentId}

mapper.select:
